package view.plantilla;import view.plantilla.utilidades.Flow;import view.utilidades.ADFUtils;/** * Objeto de metodo de guardado. * * @author omargo33@hotmail.com * */public class ItemFrgHijoBean extends BaseBean {    @SuppressWarnings("compatibility:2677241157027247979")    private static final long serialVersionUID = 1L;    /**     * Metodo para crear el objeto.     *     */    public ItemFrgHijoBean() {        super();        setNombreBundle(Flow.BUNDLE);    }    /**     * Accion Guardar.     *     * Si el guardado es sobre un master se queda en la misma pantalla y refresca.     *     * Caso contrario vuelve al listado.     *     *     * @return     */    public String actionGuardar() {        if (ADFUtils.commitRollback(getBindings(), Flow.ACCION_COMMIT, Flow.ACCION_ROLLBACK,                                    getBundle("msg_guardar_ko"), getBundle("msg_guardar_ok"))) {            setAccionEstadoTaskFlowHijo(TASK_FLOW_EDITAR);        }        return Flow.FLOW_INICIO;    }    /**     * Accion Borrar.     * @return     */    public String actionDelete() {        ADFUtils.ejecutaFormulario(getBindings(), Flow.ACCION_DELETE, Flow.ACCION_COMMIT, Flow.ACCION_ROLLBACK,                                   getBundle("msg_borrar_ko"), getBundle("msg_borrar_ok"));        return Flow.FLOW_INICIO;    }}